INSERT INTO Solution_Directory SET solId='11497',partNumber='',solName='CoreconLink For SAP Business One',solDesc='Corecon 2006 is a web-based suite of software for the architect, engineer and contractor (AEC) industry that handles estimating, project management, and job cost control. The CoreconLink For SAP Business One is designed to bridge the gap between Corecon 2006 and SAP Business One. The benefits of this combined solution is that project managers can run their jobs and keep track of their progress payments and costs whether they are out in the field or the main office. In the accounting department, the CoreconLink is then used to transfer these project financial transactions directly to SAP Business One, eliminating double entry of data and distributing the work load where it is needed.',partComSite='',solFocus='SAP Business One',sapCertSince='',lastReviewBySAP='',averTrainEndUser='2',averImplTrainingDays='2',averImplEffort='10',averImplDuration='1',averSizeImplTeam='1',averSaleCycle='1',noCustomers='0',smallImpl='1',largeImpl='39',smallImplTime='2',largeImplTime='15',smallImplTeamNo='1',largeImplTeamNo='2',solSite='http://www.corecon.com/',refCustAvailForUse='Yes',totalAppBaseLinePrice='',appPriceEur='',hardwareCost=0,hardwareCostEur=0,averLicensePrice=720,averLicensePriceEur=0,addServiceCost=0,addServicePriceEur=0,implCost=3000,implCostEur=0,sapDiscount='Yes',dbUsed='Microsoft SQL Server 2000',SAPBusUsed='Yes',SAPGUIUsed='No',compA1B1Used='',thirdPartyUsed='No',thirdPartyName='',otherIT='Corecon 2006: Microsoft ASP.NET 2.0 Web-based solution CoreconLink For SAP Business One: Microsoft.NET 1.1 Windows-based solution (Download from Corecon 2006)',addRemarks='Corecon 2006 utilizes Microsoft?s second generation Web platform, ASP.NET 2.0. Some of the salient features of Corecon are listed below. Cost Estimating: - Estimate Takeoff - Bid Management for Subs/Suppliers Project Management: - Document Control - Quality Control - Safety - Contract Administration - Job Cost Control - Scheduling Other Features: - Collaboration for both Estimating and Project Management - Executive Dashboard providing high level overview for business development and across projects - Built-in wizards to facilitate faster implementation - Import capabilities from Microsoft Excel - 400 + built-in reports using Microsoft SQL Reporting Services - Custom reporting using Microsoft Word and Excel - Cutting edge solution using ASP.NET 2.0 and Ajax technologies',solSAPMicroSite='',lastPartRevieDate='5/2/2006',reviewedBy='Norman Wendl',profileAdded='Norman Wendl',dateCreated='5/2/2006 20:00',modifiedBy='Norman Wendl',dadteUpdated='5/2/2006 20:00',notificationProc='Yes';
INSERT INTO Solution_Directory SET solId='11491',partNumber='',solName='Benefits Administration',solDesc='ADP offers Benefit Administration solutions that are integrated or standalone for the enrollment and administration of COBRA and Flexible Spending Accounts. When ADP acts as your COBRA administration provider, you no longer have to commit your company\'s time, personnel and other valuable resources to managing intricacies or COBRA. ADP\'s FSA Services provide an easy-to use, affordable way for your employees to contribute before-tax dollars toward health and dependent care expenses while providing tax savings to your company. ADP provides recordkeeping for tax-advantaged programs for employers including: 401(k) and 529 College Savings Plans. ADP\'s powerful, fully-integrated retirement savings products bring \"\"best in class\"\" services together to create a comprehensive package that provides peace of mind to both you and your employees. It coordinates all the primary elements of the plan for you because the payroll system is linked electronically to the 401(k) record keeping system with the financial information transmitted directly to the trust accounting system and transmitted directly to the investment managers. ADP and Alliance Capital have combined resources to offer a 529 payroll deduction program for college savings designed specifically for your employees. The fund is the largest section 529 college savings pla',partComSite=' is sponsored by the state of Rhode Island and managed by Alliance Capital. Best of all',solFocus=' it is a benefit you can offer at no cost to your employees and minimal cost to you.\",,SAP Business One,,,,,,,,,0,,,,,,,,Yes,,,,,,,,,,,,,,,,No,,,,,5/1/2006,,Jen Curran,5/1/2006 11:34,Jen Curran,5/1/2006 11:34,Yes',sapCertSince='',lastReviewBySAP='',averTrainEndUser='',averImplTrainingDays='',averImplEffort='',averImplDuration='',averSizeImplTeam='',averSaleCycle='',noCustomers='',smallImpl='',largeImpl='',smallImplTime='',largeImplT