INSERT INTO Solution_Directory SET solId='9701',partNumber='',solName='Time Bank - Payroll/General Ledger (GL) Link',solDesc='The Time Bank General Ledger (GL) Link from Integrated Design, Inc. (IDI) synchronizes payroll results seamlessly into SAP Business One. The proven Time Bank integration software is configured to meet each company?s specific accounting rules including posting earnings, taxes, deductions, net pay, direct deposits and other expenses into their Business One general ledger. Time Bank is easy to implement with built-in system specific domain knowledge gained through IDI?s relationships with leading payroll providers including ADP and Paychex. IDI?s Time Bank GL Link helps clients focus on running their business by eliminating time consuming and error prone data entry in multiple applications.',partComSite='',solFocus='SAP Business One',sapCertSince='',lastReviewBySAP='',averTrainEndUser='0',averImplTrainingDays='1',averImplEffort='0',averImplDuration='0',averSizeImplTeam='1',averSaleCycle='0',noCustomers='1',smallImpl='1',largeImpl='1',smallImplTime='1',largeImplTime='1',smallImplTeamNo='1',largeImplTeamNo='1',solSite='www.timebank.com',refCustAvailForUse='No',totalAppBaseLinePrice='1250',appPriceEur='',hardwareCost=0,hardwareCostEur=0,averLicensePrice=1250,averLicensePriceEur=0,addServiceCost=250,addServicePriceEur=0,implCost=350,implCostEur=0,sapDiscount='Yes',dbUsed='MSDE',SAPBusUsed='Yes',SAPGUIUsed='No',compA1B1Used='',thirdPartyUsed='Yes',thirdPartyName='ADP, Inc. (for ADP payroll interfaces)',otherIT='',addRemarks='IDI offers an online qualification utility for the Time Bank GL Link (www.timebank.com/sap). Please have your prospect/client complete the online survey about their payroll vendor and set-up. IDI will then contact you with pricing and next steps for the client. If the client is using ADP payroll, the IDI Time Bank GL Link is purchased and serviced through ADP.',solSAPMicroSite='',lastPartRevieDate='3/24/2006',reviewedBy='Kit Dick