INSERT INTO Solution_Directory SET solId='9376',partNumber='',solName='Azur for SAP - Azur Manufacturing',solDesc='The Manufacturing Module is designed to provide a comprehensive production planning and control system and uses the existing functionality within SAP Business One Sales Order Processing, Inventory, Bills of Material, and Production and adds additional functionality for Process Routing, Production and Material Planning and Control, Works Order and Work in Progress Control. Azur Manufacturing provides an easy-to-use decision support tool for production planners. Unlike wallboards, spreadsheets or simple electronic planning boards, it is interactive, intelligent and can optionally be customised to meet the specific needs of the customer. Some of the features include:- - Full graphical Drag and Drop schedule manipulation. - Full supply / demand netting. - Automatic order generation from Planning based on Customer demand. - Realistic scheduling of orders based on finite and infinite capacity. - Creation / maintenance process routes operations and times.',partComSite='http://cpsn-channel.sap.com',solFocus='SAP Business One',sapCertSince='',lastReviewBySAP='',averTrainEndUser='2',averImplTrainingDays='5',averImplEffort='12',averImplDuration='1',averSizeImplTeam='2',averSaleCycle='1',noCustomers='2',smallImpl='2',largeImpl='4',smallImplTime='8',largeImplTime='15',smallImplTeamNo='2',largeImplTeamNo='2',solSite='http://www.azurforsap.co.uk/businessone.php',refCustAvailForUse='No',totalAppBaseLinePrice='',appPriceEur='',hardwareCost=0,hardwareCostEur=0,averLicensePrice=0,averLicensePriceEur=0,addServiceCost=0,addServicePriceEur=0,implCost=0,implCostEur=0,sapDiscount='',dbUsed='',SAPBusUsed='Yes',SAPGUIUsed='Yes',compA1B1Used='',thirdPartyUsed='Yes',thirdPartyName='Preactor',otherIT='',addRemarks='',solSAPMicroSite='',lastPartRevieDate='1/26/2006',reviewedBy='David Metcalfe',profileAdded='Greg.Hackney',dateCreated='1/26/2006 7:03',modifiedBy='Greg.Hackney',dadteUp