INSERT INTO Solution_Directory SET solId='8747',partNumber='',solName='Alliance Distribution Edition for SAP Business One',solDesc='The Alliance Distribution Edition for SAP Business One is a seamlessly integrated business solution, designed for small and midsized businesses and divisions and subsidiaries of large organizations, specially developed to enable wholesale distributors to streamline critical business processes, make fast decisions based on real-time information and maintain a competitive advantage in the global market. With built-in tools to streamline managerial processes, a wireless warehouse management system with a ?pick, pack and ship? state of mind, document management and electronic data interchange capabilities that will help make your business truly paperless, and powerful financial management modules such as credit card authorization, the solution makes it easy for everyone in your company to obtain the operational and decision support they need. The Alliance Distribution Edition for SAP Business One was designed specifically for small and midsized businesses and divisions and subsidiaries of global enterprises. It is easy to purchase, easy to implement, easy to use and easy to maintain with assistance from one business advisor and one call for technical support.',partComSite='http://cpsn-channel.sap.com/alliance',solFocus='SAP Business One',sapCertSince='12/20/2005',lastReviewBySAP='12/20/2005',averTrainEndUser='10',averImplTrainingDays='20',averImplEffort='30',averImplDuration='2',averSizeImplTeam='5',averSaleCycle='5',noCustomers='100',smallImpl='5',largeImpl='75',smallImplTime='14',largeImplTime='35',smallImplTeamNo='3',largeImplTeamNo='5',solSite='www.rsmmcgladrey.com/allianceeditions',refCustAvailForUse='Yes',totalAppBaseLinePrice='80000',appPriceEur='66',hardwareCost=30000,hardwareCostEur=25,averLicensePrice=5000,averLicensePriceEur=4,addServiceCost=10000,addServicePriceEur=8,implCost=50000,implCostEur=41,sapDiscount='Yes',dbUsed='SQL',S